'use client'

import { useState, useMemo } from 'react'
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter, DialogDescription } from "@/components/ui/dialog"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { MapTileData } from "@/lib/services/game.service"
import { MapCoordinates, calculateDistance, formatDuration, calculateTravelTime } from "@/lib/utils/map-utils"
import { useRouter } from 'next/navigation'
import { Sword, Eye, Truck, Flag, MapPin, User, Shield } from 'lucide-react'

interface TileDetailsDialogProps {
  tile: MapTileData | null
  city: number
  district: number
  userProperty: {
    city: number
    district: number
    building: number
  } | null
  isOpen: boolean
  onClose: () => void
}

export function TileDetailsDialog({ tile, city, district, userProperty, isOpen, onClose }: TileDetailsDialogProps) {
  const router = useRouter()

  const distance = useMemo(() => {
    if (!tile || !userProperty) return 0;
    const origin: MapCoordinates = {
      city: userProperty.city,
      district: userProperty.district,
      building: userProperty.building
    };
    const target: MapCoordinates = {
      city: city,
      district: district,
      building: tile.coordenada_edificio
    };
    return calculateDistance(origin, target);
  }, [tile, city, district, userProperty]);

  // Estimate travel time with a base speed (e.g. 100) just for display
  const estimatedTime = calculateTravelTime(distance, 100);

  if (!tile) return null;

  const handleAction = (action: string) => {
    // Redirigir a /dashboard/missions
    // Params: type, target=C:B:E
    const targetStr = `${city}:${district}:${tile.coordenada_edificio}`;
    router.push(`/dashboard/missions?type=${action}&target=${targetStr}`);
    onClose();
  };

  const isOwn = tile.es_propia;
  const isEmpty = tile.usuario_id === '00000000-0000-0000-0000-000000000000'; // Assuming empty tiles have null/zero user, but check logic.
  // Actually, get_map_tiles might return null for user fields if empty.
  // Let's check MapTileData type. It has usuario_id string.
  // Usually the backend might filter out empty spots or return them with placeholders.
  // We'll assume if points == 0 or specific check.
  // Wait, legacy code usually treats 0-level or specific ID as empty.
  // Let's assume non-empty if usuario_id is valid.

  // Re-reading MapTileData: usuario_id is string.
  // If it's a free slot, does it appear in get_map_tiles?
  // get_map_tiles returns properties. If a slot is empty, is it returned?
  // Usually map RPCs return ALL slots 1-255 or just occupied ones.
  // If just occupied, then we can't click empty slots unless we render a full grid and fill gaps.
  // CityMap receives `tiles` which is MapTileData[].
  // If we want to click "Empty" slots, CityMap needs to render them.
  // We will address that in CityMap.
  // Here, we assume `tile` is passed. If it's a "virtual" empty tile, we need to handle it.

  // Let's support a "virtual" tile object for empty slots passed from CityMap
  const isOccupied = tile.usuario_id && tile.usuario_id !== '00000000-0000-0000-0000-000000000000';

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[425px] bg-stone-900 text-stone-100 border-stone-700">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-xl text-amber-500">
            <MapPin className="h-5 w-5" />
            Solar {city}:{district}:{tile.coordenada_edificio}
          </DialogTitle>
          <DialogDescription className="text-stone-400">
            {isOccupied ? "Propiedad Ocupada" : "Solar Disponible"}
          </DialogDescription>
        </DialogHeader>

        <div className="grid gap-4 py-4">
          {isOccupied ? (
            <div className="space-y-3">
              <div className="flex items-center gap-3 p-3 bg-stone-800 rounded-lg">
                <User className="h-8 w-8 text-stone-400" />
                <div>
                  <p className="font-medium text-stone-200">{tile.nombre_usuario}</p>
                  {tile.nombre_familia && (
                    <Badge variant="outline" className="text-xs border-amber-500/50 text-amber-500 mt-1">
                      [{tile.etiqueta_familia}] {tile.nombre_familia}
                    </Badge>
                  )}
                </div>
              </div>

              <div className="flex justify-between items-center p-3 bg-stone-800/50 rounded-lg">
                <span className="text-sm text-stone-400">Puntos</span>
                <span className="font-mono text-stone-200">{tile.puntos.toLocaleString()}</span>
              </div>
            </div>
          ) : (
            <div className="p-4 bg-stone-800/30 rounded-lg border border-stone-700 border-dashed text-center text-stone-500">
              Este solar está disponible para colonizar o fundar una nueva propiedad.
            </div>
          )}

          <div className="grid grid-cols-2 gap-2 text-sm">
            <div className="p-2 bg-stone-950 rounded border border-stone-800">
              <span className="block text-xs text-stone-500 mb-1">Distancia</span>
              <span className="font-mono">{distance} km</span>
            </div>
            <div className="p-2 bg-stone-950 rounded border border-stone-800">
              <span className="block text-xs text-stone-500 mb-1">Tiempo Est.</span>
              <span className="font-mono">~{formatDuration(estimatedTime)}</span>
            </div>
          </div>
        </div>

        <DialogFooter className="grid grid-cols-2 gap-2 sm:gap-2">
          {isOwn ? (
            <Button variant="default" className="col-span-2 bg-green-600 hover:bg-green-700" onClick={() => router.push('/dashboard/overview')}>
              Gestionar Propiedad
            </Button>
          ) : isOccupied ? (
            <>
              <Button variant="outline" className="border-stone-600 hover:bg-stone-800" onClick={() => handleAction('espiar')}>
                <Eye className="mr-2 h-4 w-4" /> Espiar
              </Button>
              <Button variant="outline" className="border-stone-600 hover:bg-stone-800" onClick={() => handleAction('transportar')}>
                <Truck className="mr-2 h-4 w-4" /> Transportar
              </Button>
              <Button variant="destructive" className="col-span-2 bg-red-900/80 hover:bg-red-900 border-red-800" onClick={() => handleAction('atacar')}>
                <Sword className="mr-2 h-4 w-4" /> Atacar
              </Button>
            </>
          ) : (
            <Button className="col-span-2 bg-amber-600 hover:bg-amber-700" onClick={() => handleAction('colonizar')}>
              <Flag className="mr-2 h-4 w-4" /> Colonizar / Fundar
            </Button>
          )}
        </DialogFooter>
      </DialogContent>
    </Dialog>
  )
}

import { createClient } from '@/lib/supabase/server'
import { Propiedad, AtaqueEntrante, ColaMisiones, MiembroFamilia, Familia } from '@/types/database'
import { RespuestaConstruccion, ColaConstruccion, DashboardData } from '@/types/game'

interface CreateInitialPropertyData {
  nombre: string
  ciudad: number
  barrio: number
  edificio: number
}

interface CreateInitialPropertyResponse {
  success?: boolean
  error?: string
  propiedad_id?: string
}

interface ResearchResponse {
  success?: boolean
  error?: string
}

interface RecruitResponse {
  success?: boolean
  error?: string
}

interface MissionData {
  propiedad_origen_id: string
  tipo_mision: 'atacar' | 'transportar' | 'espiar' | 'colonizar' | 'recolectar'
  tropas: Record<string, number>
  recursos?: Record<string, number>
  destino_ciudad: number
  destino_barrio: number
  destino_edificio: number
  velocidad_flota?: number
}

interface MissionResponse {
  success?: boolean
  error?: string
  mision_id?: string
}

export interface MapTileData {
  propiedad_id: string
  coordenada_edificio: number
  usuario_id: string
  nombre_usuario: string
  nombre_familia: string | null
  etiqueta_familia: string | null
  puntos: number
  es_propia: boolean
}

export async function getMapTiles(city: number, district: number): Promise<MapTileData[]> {
  const supabase = await createClient()

  const { data, error } = await supabase.rpc('get_map_tiles', {
    p_ciudad: city,
    p_barrio: district
  })

  if (error) {
    console.error('Error fetching map tiles:', error)
    return []
  }

  return data as MapTileData[]
}

export async function createInitialProperty(data: CreateInitialPropertyData): Promise<CreateInitialPropertyResponse> {
  const supabase = await createClient()

  const { data: result, error } = await supabase.rpc('crear_propiedad_inicial', {
    p_nombre: data.nombre,
    p_ciudad: data.ciudad,
    p_barrio: data.barrio,
    p_edificio: data.edificio
  })

  if (error) {
    console.error('Error creating initial property:', error)
    return { error: error.message }
  }

  return result as CreateInitialPropertyResponse
}

export async function getUserProperty(userId: string): Promise<string | null> {
  const supabase = await createClient()
  const { data, error } = await supabase.from('propiedad').select('id').eq('usuario_id', userId).maybeSingle()

  if (error) {
    console.error('Error checking user property:', error)
    return null
  }
  return data?.id || null
}

export async function getDashboardData(propertyId: string): Promise<DashboardData | null> {
  const supabase = await createClient()

  const { data: dashboardData, error } = await supabase.rpc('get_dashboard_data', {
    p_propiedad_id: propertyId
  })

  if (error || !dashboardData) {
    console.error('Error fetching dashboard data:', error)
    return null
  }

  // Fetch real-time collections in parallel
  const [attacks, missions] = await Promise.all([
    getIncomingAttacks(propertyId),
    getActiveMissions(propertyId)
  ])

  // Merge into expanded DashboardData structure
  const result: DashboardData = {
    ...dashboardData,
    incoming_attacks: attacks,
    cola_misiones: missions,
    tropa_usuario: dashboardData.tropas || [],
    cola_reclutamiento: dashboardData.colas?.reclutamiento || []
  }

  return result
}

export async function getIncomingAttacks(propertyId: string): Promise<AtaqueEntrante[]> {
  const supabase = await createClient()

  // First get the user_id for this property to filter attacks targeting this user
  const { data: prop, error: propError } = await supabase
    .from('propiedad')
    .select('usuario_id')
    .eq('id', propertyId)
    .single()

  if (propError || !prop) {
      console.error('Error getting property owner for attacks:', propError)
      return []
  }

  const { data, error } = await supabase
    .from('ataque_entrante')
    .select('*')
    .eq('defensor_id', prop.usuario_id)
    .order('fecha_llegada', { ascending: true })

  if (error) {
    console.error('Error fetching incoming attacks:', error)
    return []
  }

  return data as AtaqueEntrante[]
}

export async function getActiveMissions(propertyId: string): Promise<ColaMisiones[]> {
  const supabase = await createClient()
  const { data, error } = await supabase
      .from('cola_misiones')
      .select('*')
      .eq('propiedad_origen_id', propertyId)
      .order('fecha_llegada', { ascending: true })

  if (error) {
      console.error('Error fetching active missions:', error)
      return []
  }

  return data as ColaMisiones[]
}

export async function getFamilyInfo(userId: string): Promise<{ miembro: MiembroFamilia, familia: Familia } | null> {
  const supabase = await createClient()
  const { data: miembro, error: mError } = await supabase
      .from('miembro_familia')
      .select('*')
      .eq('usuario_id', userId)
      .maybeSingle()

  if (mError || !miembro) return null

  const { data: familia, error: fError } = await supabase
      .from('familia')
      .select('*')
      .eq('id', miembro.familia_id)
      .single()

  if (fError || !familia) return null

  return { miembro, familia }
}

export async function cancelConstruction(queueId: string): Promise<{ success: boolean; error?: string }> {
  const supabase = await createClient()

  const { data, error } = await supabase.rpc('cancelar_construccion', {
    p_cola_id: queueId
  })

  if (error) {
    console.error('Error cancelling construction:', error)
    return { success: false, error: error.message }
  }

  const result = data as { success: boolean; error?: string }
  if (result.error) {
    return { success: false, error: result.error }
  }

  return { success: true }
}

export async function syncResources(propertyId: string): Promise<Propiedad | null> {
  const supabase = await createClient()

  // 1. Process queues (construction, research, etc.) for this property
  const { error: queueError } = await supabase.rpc('procesar_colas_propiedad', {
    p_propiedad_id: propertyId
  })

  if (queueError) {
    console.error('Error processing queues:', queueError)
    // Continue anyway to at least try to get resources
  }

  // 2. Materialize resources (lazy update)
  const { error: rpcError } = await supabase.rpc('materializar_recursos', {
    p_propiedad_id: propertyId
  })

  if (rpcError) {
    console.error('Error synchronizing resources:', rpcError)
  }

  // 3. Fetch the updated property data
  const { data, error } = await supabase
    .from('propiedad')
    .select('*')
    .eq('id', propertyId)
    .single()

  if (error) {
    console.error('Error fetching property:', error)
    return null
  }

  return data
}

export async function startConstruction(propertyId: string, buildingId: string): Promise<RespuestaConstruccion> {
  const supabase = await createClient()

  const { data, error } = await supabase.rpc('iniciar_construccion_habitacion', {
    p_propiedad_id: propertyId,
    p_habitacion_id: buildingId
  })

  if (error) {
    console.error('Error starting construction:', error)
    return { success: false, error: error.message }
  }

  return data as RespuestaConstruccion
}

export async function getConstructionQueue(propertyId: string): Promise<ColaConstruccion[]> {
  const supabase = await createClient()

  const { data, error } = await supabase
    .from('cola_construccion')
    .select('id, propiedad_id, habitacion_id, nivel_destino, fecha_fin')
    .eq('propiedad_id', propertyId)
    .order('fecha_fin', { ascending: true })

  if (error) {
    console.error('Error fetching construction queue:', error)
    return []
  }

  return data as ColaConstruccion[]
}

export async function startResearch(propertyId: string, researchId: string): Promise<ResearchResponse> {
  const supabase = await createClient()

  const { data, error } = await supabase.rpc('iniciar_entrenamiento', {
    p_propiedad_id: propertyId,
    p_entrenamiento_id: researchId
  })

  if (error) {
    console.error('Error starting research:', error)
    return { success: false, error: error.message }
  }

  return data as ResearchResponse
}

export async function recruitTroops(propertyId: string, troopId: string, amount: number): Promise<RecruitResponse> {
  const supabase = await createClient()

  const { data, error } = await supabase.rpc('iniciar_reclutamiento', {
    p_propiedad_id: propertyId,
    p_tropa_id: troopId,
    p_cantidad: amount
  })

  if (error) {
    console.error('Error recruiting troops:', error)
    return { success: false, error: error.message }
  }

  return data as RecruitResponse
}

export async function launchMission(missionData: MissionData): Promise<MissionResponse> {
  const supabase = await createClient()

  const { data, error } = await supabase.rpc('iniciar_mision', {
    p_propiedad_origen_id: missionData.propiedad_origen_id,
    p_tipo_mision: missionData.tipo_mision,
    p_tropas: missionData.tropas,
    p_recursos: missionData.recursos || {},
    p_destino_ciudad: missionData.destino_ciudad,
    p_destino_barrio: missionData.destino_barrio,
    p_destino_edificio: missionData.destino_edificio,
    p_velocidad_flota: missionData.velocidad_flota || 100
  })

  if (error) {
    console.error('Error launching mission:', error)
    return { success: false, error: error.message }
  }

  return data as MissionResponse
}

