'use client';

import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Clock, Hammer } from "lucide-react";
import { ColaDetalle } from "@/types/game";
import { useEffect, useState } from "react";

interface ConstructionQueueListProps {
  queue: ColaDetalle[];
}

export function ConstructionQueueList({ queue }: ConstructionQueueListProps) {
  const [now, setNow] = useState<number>(Date.now());

  useEffect(() => {
    if (queue.length === 0) return;
    const interval = setInterval(() => setNow(Date.now()), 1000);
    return () => clearInterval(interval);
  }, [queue.length]);

  if (!queue || queue.length === 0) {
    return null;
  }

  return (
    <Card className="border-amber-500/30 bg-amber-950/10">
      <CardContent className="p-4 space-y-3">
        <div className="flex items-center gap-2 mb-2">
            <Hammer className="w-5 h-5 text-amber-600" />
            <h3 className="font-bold text-amber-800 dark:text-amber-500">Cola de Construcción</h3>
            <Badge variant="outline" className="ml-auto border-amber-500/50 text-amber-700 dark:text-amber-400">
                {queue.length} / 5
            </Badge>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-3">
            {queue.map((item) => {
                const endTime = new Date(item.fecha_fin).getTime();
                // Assuming start time is not available in ColaDetalle, we can verify this later.
                // For now, let's just show time remaining.
                const timeLeft = Math.max(0, endTime - now);
                const secondsLeft = Math.floor(timeLeft / 1000);

                const hours = Math.floor(secondsLeft / 3600);
                const minutes = Math.floor((secondsLeft % 3600) / 60);
                const seconds = secondsLeft % 60;

                const timeString = `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;

                return (
                    <div key={item.id} className="bg-card border border-border/50 rounded p-2 flex flex-col gap-1 shadow-sm">
                        <div className="flex justify-between items-start">
                            <span className="font-semibold text-sm truncate pr-2">{item.nombre}</span>
                            <span className="text-xs font-mono bg-muted px-1 rounded">{timeString}</span>
                        </div>
                        <div className="flex justify-between items-center text-xs text-muted-foreground">
                            <span>Nivel {item.nivel_destino}</span>
                            <Clock className="w-3 h-3" />
                        </div>
                        {/* We don't have total duration to calculate progress percentage accurately without knowing start time. 
                            If we want a progress bar, we need to know when it started or total duration. 
                            For now, maybe just a visual indicator that it is active. 
                        */}
                         <Progress value={100} className="h-1 mt-1 animate-pulse" /> 
                    </div>
                );
            })}
        </div>
      </CardContent>
    </Card>
  );
}

'use client';

import Image from "next/image";
import { Button } from "@/components/ui/button";
import { PlaceHolderImages } from "@/lib/placeholder-images";
import { Coins, Shell, Droplets } from "lucide-react";
import type { Room } from "@/lib/data/rooms-data";
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { upgradeBuildingAction, cancelConstructionAction } from "@/actions/game.actions";
import { useToast } from "@/hooks/use-toast";
import { useState, useOptimistic, useTransition } from "react";
import { ColaDetalle } from "@/types/game";
import { Progress } from "@/components/ui/progress";

type RoomCardProps = {
  room: Room;
  propiedadId?: string;
  dynamicLevel: number;
  construction?: ColaDetalle;
  isQueueFull?: boolean;
};

const ResourceIcon = ({ type }: { type: string }) => {
    const iconClass = "w-3 h-3 md:w-4 md:h-4 mr-1";
    switch (type) {
        case 'Armas':
            return <Shell className={`${iconClass} text-stone-600`} />;
        case 'Municion':
            return <Coins className={`${iconClass} text-yellow-600`} />;
        case 'Dolares':
            return <span className={`${iconClass} font-bold text-green-700 flex items-center justify-center`}>$</span>;
        default:
            return null;
    }
}

export function RoomCard({ room, propiedadId, dynamicLevel, construction, isQueueFull = false }: RoomCardProps) {
  const image = PlaceHolderImages.find((p) => p.id === room.image) || PlaceHolderImages.find(p => p.id === 'dark-alley');
  const { toast } = useToast();
  const [isPending, startTransition] = useTransition();

  const handleUpgrade = async () => {
    if (!propiedadId) return;

    startTransition(async () => {
      const result = await upgradeBuildingAction(room.id);
      if (result.success) {
        toast({ title: "Construcción iniciada", description: `Mejorando ${room.name}.` });
      } else {
        toast({ title: "Error", description: result.error, variant: "destructive" });
      }
    });
  };

  const handleCancel = async () => {
      if (!construction) return;
      startTransition(async () => {
          const result = await cancelConstructionAction(construction.id);
          if (result.success) {
              toast({ title: "Cancelado", description: "Construcción cancelada." });
          } else {
              toast({ title: "Error", description: result.error, variant: "destructive" });
          }
      });
  };

  const isConstructing = !!construction;

  return (
    <Card className="flex flex-col sm:flex-row overflow-hidden hover:shadow-lg transition-shadow duration-300 border-primary/20 bg-card">
      {/* Image Section */}
      <div className="relative w-full sm:w-1/3 min-h-[160px] sm:min-h-full">
        {image && (
            <Image
            src={image.imageUrl}
            alt={image.description}
            fill
            className="object-cover"
            data-ai-hint={image.imageHint}
            sizes="(max-width: 640px) 100vw, 33vw"
            />
        )}
        <div className="absolute top-2 right-2 sm:hidden">
             <Badge variant="secondary" className="font-bold">Nv. {dynamicLevel}</Badge>
        </div>
      </div>

      {/* Content Section */}
      <div className="flex-1 flex flex-col justify-between p-4 gap-3">
        <div className="space-y-2">
            <div className="flex justify-between items-start">
                <h3 className="font-bold text-lg md:text-xl text-primary font-headline leading-tight">
                {room.name}
                </h3>
                <Badge variant="outline" className="hidden sm:inline-flex border-primary/50 text-primary font-bold">
                    Nv. {dynamicLevel}
                </Badge>
            </div>

            <p className="text-sm text-muted-foreground line-clamp-2 md:line-clamp-3">
                {room.description}
            </p>
        </div>

        {/* Resources & Action Footer */}
        <div className="space-y-3 pt-2 border-t border-border/50">
             <div className="flex flex-wrap gap-x-4 gap-y-2 text-xs md:text-sm text-muted-foreground">
                {Object.entries(room.costs).map(([resource, value]) => (
                    <div key={resource} className="flex items-center font-medium">
                        <ResourceIcon type={resource} />
                        <span>{value}</span>
                    </div>
                ))}
                <div className="flex items-center ml-auto text-xs font-mono bg-secondary/30 px-2 py-0.5 rounded">
                    ⏱ {room.duration}
                </div>
            </div>

            <div className="flex justify-end pt-1">
                {isConstructing ? (
                <div className="w-full space-y-2">
                    <div className="flex justify-between text-xs">
                        <span className="font-bold text-amber-600">En construcción...</span>
                        <span>{new Date(construction.fecha_fin).toLocaleTimeString()}</span>
                    </div>
                    {/* Simplified progress bar or countdown could be here */}
                    <Button
                        variant="destructive"
                        size="sm"
                        className="w-full h-7 text-xs"
                        onClick={handleCancel}
                        disabled={isPending}
                    >
                        {isPending ? "Cancelando..." : "Cancelar"}
                    </Button>
                </div>
                ) : (
                <Button
                    className="w-full sm:w-auto"
                    size="sm"
                    onClick={handleUpgrade}
                    disabled={isPending || !propiedadId || isQueueFull}
                    title={isQueueFull ? "Cola de construcción llena (5/5)" : "Ampliar edificio"}
                >
                    {isPending ? "Iniciando..." : (isQueueFull ? "Cola Llena" : "Ampliar")}
                </Button>
                )}
            </div>
        </div>
      </div>
    </Card>
  );
}

'use client';

import React, { createContext, useContext, ReactNode } from 'react';
import { DashboardData } from '@/types/game';
import { useGameRealtime } from '@/hooks/use-game-realtime';

interface GameStateContextType {
  gameState: DashboardData | null;
}

const GameStateContext = createContext<GameStateContextType | undefined>(undefined);

interface GameStateProviderProps {
  initialData: DashboardData | null;
  children: ReactNode;
}

export function GameStateProvider({ initialData, children }: GameStateProviderProps) {
  const gameState = useGameRealtime(initialData);

  return (
    <GameStateContext.Provider value={{ gameState }}>
      {children}
    </GameStateContext.Provider>
  );
}

export function useGameState() {
  const context = useContext(GameStateContext);
  if (context === undefined) {
