import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';

export async function GET(request: NextRequest) {
  const authHeader = request.headers.get('authorization');
  if (authHeader !== `Bearer ${process.env.CRON_SECRET}`) {
    return new NextResponse('Unauthorized', { status: 401 });
  }

  // Create a Supabase client with the Service Role Key to bypass RLS
  // The 'procesar_colas' function might not be SECURITY DEFINER, so we need admin privileges.
  const supabaseAdmin = createClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.SUPABASE_SERVICE_ROLE_KEY!
  );

  try {
    const { error } = await supabaseAdmin.rpc('procesar_colas');

    if (error) {
      console.error('Error processing queues:', error);
      return NextResponse.json({ error: error.message }, { status: 500 });
    }

    return NextResponse.json({ processed: true });
  } catch (err: any) {
    console.error('Unexpected error processing queues:', err);
    return NextResponse.json({ error: err.message }, { status: 500 });
  }
}

import { createClient } from '@/lib/supabase/server'
import { NextResponse } from 'next/server'

export async function GET(request: Request) {
  const { searchParams, origin } = new URL(request.url)
  const code = searchParams.get('code')
  const next = searchParams.get('next') ?? '/'

  if (code) {
    const supabase = createClient()
    const { error } = await supabase.auth.exchangeCodeForSession(code)
    if (!error) {
        // For password recovery, Supabase redirects to the specified URL.
        // After code exchange, we can check for user and redirect to a reset password page.
        const { data: { user } } = await supabase.auth.getUser()
        if (searchParams.get('type') === 'recovery') {
          return NextResponse.redirect(`${origin}/reset-password`)
        }
        return NextResponse.redirect(`${origin}${next}`)
    }
  }

  // return the user to an error page with instructions
  return NextResponse.redirect(`${origin}/login?message=No se pudo autenticar al usuario.`)
}

