import { useEffect, useState, useRef } from 'react';
import { createClient } from '@/lib/supabase/client';
import { DashboardData } from '@/types/game';
import { getDashboardDataAction } from '@/actions/game.actions';

export function useGameRealtime(initialData: DashboardData | null) {
  const [data, setData] = useState<DashboardData | null>(initialData);
  const supabase = createClient();

  // Ref for client-side interpolation state
  const lastUpdateRef = useRef<number>(Date.now());
  const interpolationIntervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (!initialData?.propiedad.id) return;

    const propertyId = initialData.propiedad.id;

    // Helper to refresh full data on complex events
    const refreshData = async () => {
      const result = await getDashboardDataAction();
      if (result.success && result.data) {
        setData(result.data);
        lastUpdateRef.current = Date.now();
      }
    };

    // Subscription
    const channel = supabase.channel('game-dashboard')
      .on(
        'postgres_changes',
        {
          event: 'UPDATE',
          schema: 'public',
          table: 'propiedad',
          filter: `id=eq.${propertyId}`,
        },
        (payload) => {
          // Optimistic/Partial update for property resources
          setData((prev) => {
            if (!prev) return null;
            return {
              ...prev,
              propiedad: { ...prev.propiedad, ...payload.new as any },
              // Note: Resources val/max/prod might need recalibration if prod changed.
              // But 'propiedad' row update usually means resources were materialized (updated to now).
              // So we take the new values.
              recursos: {
                ...prev.recursos,
                armas: { ...prev.recursos.armas, val: payload.new.armas },
                municion: { ...prev.recursos.municion, val: payload.new.municion },
                alcohol: { ...prev.recursos.alcohol, val: payload.new.alcohol },
                dolares: { ...prev.recursos.dolares, val: payload.new.dolares },
              }
            };
          });
          lastUpdateRef.current = Date.now();
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'cola_construccion',
          filter: `propiedad_id=eq.${propertyId}`,
        },
        () => {
          refreshData();
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'habitacion_usuario',
          filter: `propiedad_id=eq.${propertyId}`,
        },
        () => {
          refreshData();
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [initialData?.propiedad.id, supabase]);

  // Interpolation effect
  useEffect(() => {
    if (!data) return;

    interpolationIntervalRef.current = setInterval(() => {
      const now = Date.now();
      const elapsedSeconds = (now - lastUpdateRef.current) / 1000;

      // Don't interpolate if elapsed time is negligible or too large (tab inactive)
      if (elapsedSeconds < 0.1 || elapsedSeconds > 5) return;

      setData((prev) => {
        if (!prev) return null;

        const interpolate = (current: number, max: number, prodPerHour: number) => {
          const prodPerSec = prodPerHour / 3600;
          const added = prodPerSec * elapsedSeconds;
          return Math.min(max, current + added);
        };

        return {
          ...prev,
          recursos: {
            ...prev.recursos,
            armas: { ...prev.recursos.armas, val: interpolate(prev.recursos.armas.val, prev.recursos.armas.max, prev.recursos.armas.prod) },
            municion: { ...prev.recursos.municion, val: interpolate(prev.recursos.municion.val, prev.recursos.municion.max, prev.recursos.municion.prod) },
            alcohol: { ...prev.recursos.alcohol, val: interpolate(prev.recursos.alcohol.val, prev.recursos.alcohol.max, prev.recursos.alcohol.prod) },
            dolares: { ...prev.recursos.dolares, val: interpolate(prev.recursos.dolares.val, prev.recursos.dolares.max, prev.recursos.dolares.prod) },
          }
        };
      });

      lastUpdateRef.current = now;
    }, 1000); // Update every second

    return () => {
      if (interpolationIntervalRef.current) {
        clearInterval(interpolationIntervalRef.current);
      }
    };
  }, [data?.recursos.armas.prod]); // Re-run if production rates change (e.g. after refreshData)

  return data;
}

import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

"use client"

// Inspired by react-hot-toast library
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

'use server'

import { createClient } from '@/lib/supabase/server'
import { headers } from 'next/headers'
import { redirect } from 'next/navigation'
import { z } from 'zod'

const loginSchema = z.object({
  email: z.string().email({ message: 'Correo electrónico inválido' }),
  password: z.string().min(6, { message: 'La contraseña debe tener al menos 6 caracteres' }),
})

export async function login(prevState: any, formData: FormData) {
  const supabase = createClient()
  const validatedFields = loginSchema.safeParse(Object.fromEntries(formData.entries()))

  if (!validatedFields.success) {
    return {
      errors: validatedFields.error.flatten().fieldErrors,
      message: 'Por favor, corrige los errores en el formulario.',
    }
  }

  const { email, password } = validatedFields.data

  const { error } = await supabase.auth.signInWithPassword({
    email,
    password,
  })

  if (error) {
    return {
        message: 'Credenciales inválidas. Por favor, inténtalo de nuevo.',
    }
  }

  return redirect('/dashboard')
}

const signupSchema = z.object({
  email: z.string().email({ message: 'Correo electrónico inválido' }),
  password: z.string().min(6, { message: 'La contraseña debe tener al menos 6 caracteres' }),
})

export async function signup(prevState: any, formData: FormData) {
  const origin = (await headers()).get('origin')
  const supabase = createClient()
  const validatedFields = signupSchema.safeParse(Object.fromEntries(formData.entries()))

  if (!validatedFields.success) {
    return {
      errors: validatedFields.error.flatten().fieldErrors,
      message: 'Por favor, corrige los errores en el formulario.',
    }
  }

  const { email, password } = validatedFields.data

  const { error } = await supabase.auth.signUp({
    email,
    password,
    options: {
      emailRedirectTo: `${origin}/auth/callback`,
    },
  })

  if (error) {
    if (error.message.includes('User already registered')) {
        return { message: 'Este correo electrónico ya está registrado.'}
    }
    return { message: 'No se pudo crear la cuenta. Por favor, inténtalo de nuevo.' }
  }

  return { message: '¡Revisa tu correo electrónico para verificar tu cuenta!' }
}

const forgotPasswordSchema = z.object({
    email: z.string().email({ message: 'Correo electrónico inválido' }),
})

export async function forgotPassword(prevState: any, formData: FormData) {
    const origin = (await headers()).get('origin')
    const supabase = createClient()
    const validatedFields = forgotPasswordSchema.safeParse(Object.fromEntries(formData.entries()))

    if (!validatedFields.success) {
        return {
            errors: validatedFields.error.flatten().fieldErrors,
            message: 'Por favor, corrige los errores en el formulario.',
        }
    }

    const { email } = validatedFields.data

    const { error } = await supabase.auth.resetPasswordForEmail(email, {
        redirectTo: `${origin}/reset-password`,
    })

    if (error) {
        return { message: 'No se pudo enviar el correo de restablecimiento. Por favor, inténtalo de nuevo.' }
    }

    return { message: 'Si existe una cuenta con este correo, recibirás un enlace para restablecer tu contraseña.' }
}

const resetPasswordSchema = z.object({
    password: z.string().min(6, { message: 'La contraseña debe tener al menos 6 caracteres' }),
});

export async function resetPassword(prevState: any, formData: FormData) {
    const supabase = createClient()

    const { data: { user } } = await supabase.auth.getUser();
    if (!user) {
        return { message: 'No estás autenticado o la sesión ha expirado.' };
    }

    const validatedFields = resetPasswordSchema.safeParse(Object.fromEntries(formData.entries()));

    if (!validatedFields.success) {
        return {
            errors: validatedFields.error.flatten().fieldErrors,
            message: 'Por favor, corrige los errores en el formulario.',
        }
    }
    const { password } = validatedFields.data;

    const { error } = await supabase.auth.updateUser({ password });

    if (error) {
        return { message: 'No se pudo restablecer la contraseña. El enlace puede haber expirado.' };
    }

    redirect('/dashboard');
}

export async function socialLogin(provider: 'google' | 'github') {
  const origin = (await headers()).get('origin')
  const supabase = createClient()
  const { data, error } = await supabase.auth.signInWithOAuth({
    provider,
    options: {
      redirectTo: `${origin}/auth/callback`,
    },
  })

  if (error) {
    // This will be caught by the nearest error boundary
    return redirect(`/login?message=No se pudo iniciar sesión con ${provider}.`)
  }

  return redirect(data.url)
}

export async function signOut() {
  const supabase = createClient()
  await supabase.auth.signOut()
  redirect('/login')
}

'use server';

import { revalidatePath } from 'next/cache';
import { createClient } from '@/lib/supabase/server';
import { IniciarConstruccionSchema } from '@/types/game';

export async function iniciarConstruccionHabitacion(
  propiedad_id: string,
  habitacion_id: string
) {
  const supabase = createClient();

  // 1. Validar las entradas
  const validatedFields = IniciarConstruccionSchema.safeParse({
    propiedad_id,
    habitacion_id,
  });

  if (!validatedFields.success) {
    return {
      error: 'Campos inválidos.',
    };
  }

  const { data: parsedData } = validatedFields;

  try {
    // 2. Llamar a la función RPC de la base de datos
    const { data, error } = await supabase.rpc('iniciar_construccion_habitacion', {
      p_propiedad_id: parsedData.propiedad_id,
      p_habitacion_id: parsedData.habitacion_id,
    });

    if (error) {
      console.error('Error al iniciar construcción:', error);
      // El error de la base de datos puede contener información sensible,
      // devolvemos un mensaje genérico o uno específico si es seguro.
      return {
        error: 'No se pudo iniciar la construcción. Inténtalo de nuevo.',
      };
    }

    // El RPC puede devolver un JSON con un campo de error específico
    if (data && typeof data === 'object' && 'error' in data) {
        return {
            error: data.error,
        };
    }

    // 3. Devolver una respuesta exitosa
    revalidatePath('/dashboard/rooms');
    return {
      success: true,
      data,
    };
  } catch (e) {
    console.error('Excepción al llamar a RPC:', e);
    return {
      error: 'Ocurrió un error inesperado en el servidor.',
    };
  }
}

